import './App.css';
import React from 'react';
import { MapContainer, TileLayer, useMap } from 'react-leaflet'
import { Map, GeoJSON } from 'react-leaflet/GeoJSON'
import mapdata from "./geo.json"
import { color } from 'd3';


function App() {
  //./TOWN_MOI_1120317.json
  /*
  const geoEvent = (mapdata.features, layer) => {

  };
  */
  /*
      <TileLayer
        attribution='&copy; <a href="https://www.openstreetmap.org/copyright">OpenStreetMap</a> contributors'
        url="https://tile.openstreetmap.org/{z}/{x}/{y}.png"
      />
      */
  const {useState} = React;
  const [selectedTownName, setSelectedTownName] = useState();
  const mapFeature=(country, layer)=>{
    console.log(country);
    const townName = country.properties.TOWNNAME;
    layer.bindPopup(townName);

    layer.on({
      click: (e) => {
        setSelectedTownName(selectedTownName=e.sourceTarget.feature.properties.TOWNNAME);
      }
    });
  }
  const mapStyle={
    weight: 1,
    fillOpacity: 1,
    fillColor: "rgb(128, 206, 197)",
    color: "rgb(230,230,230)",
  };

  return (
    <div container>
      <MapContainer center={[23.6, 120.9738819]} zoom={7}>

      <GeoJSON style={mapStyle} data={mapdata} onEachFeature={mapFeature}></GeoJSON>
      </MapContainer>
      <p>{selectedTownName}</p>
    </div>
  ); 
}

export default App;
